mapper.select:
SELECT * FROM
(
	SELECT /*+ FIRST_ROWS 100 */ ROWNUM AS NUM, IP.* FROM
	(
		SELECT /*+ INDEX_DESC(P) FIRST_ROWS */ * FROM tbl_product P


				p_name LIKE '%' || #{search,jdbcType=VARCHAR} || '%'		


	) IP
	WHERE ROWNUM < = #{pageVO.limit,jdbcType=VARCHAR}
) 
WHERE NUM > = #{pageVO.offset,jdbcType=VARCHAR}

SELECT * FROM
(
	SELECT /*+ FIRST_ROWS_100 */ ROWNUM AS NUM, IP.* FROM
	(
		SELECT /*+ INDEX_DESC(P) FIRST_ROWS */ * FROM tbl_product P
	) IP
	WHERE ROWNUM < = #{limit,jdbcType=VARCHAR}
)
WHERE NUM > = #{offset,jdbcType=VARCHAR}

SELECT * FROM ( SELECT /*+ FIRST_ROWS_100 */ ROWNUM AS NUM, IP.* FROM ( SELECT /*+ INDEX_DESC(P) FIRST_ROWS */ * FROM ( SELECT P.p_code, P.p_name, P.p_price, S.s_code, S.s_size, C.c_code, C.c_color, C.c_qty, P.p_qty FROM tbl_product P,tbl_size S,tbl_color C WHERE P.p_code = S.s_p_code AND S.s_code = C.c_s_code ORDER BY P.p_code DESC )P
p_name LIKE '%' || #{search,jdbcType=VARCHAR} || '%'
) IP WHERE ROWNUM < = #{pageVO.limit,jdbcType=VARCHAR} ) WHERE NUM > = #{pageVO.offset,jdbcType=VARCHAR}

mapper.insert:
INSERT INTO tbl_product(
p_code, p_name, p_price, p_desc, p_image, p_size, p_color, p_date, p_time, p_qty
) VALUES ( SEQ_PRODUCT.NEXTVAL, #{p_name}, #{p_price}, #{p_desc,jdbcType=VARCHAR}, #{p_image,jdbcType=VARCHAR}, #{p_size,jdbcType=VARCHAR}, #{p_color,jdbcType=VARCHAR}, #{p_date}, #{p_time}, #{p_qty,jdbcType=VARCHAR} )

INSERT INTO tbl_size(s_code, s_p_code, s_size) SELECT SEQ_SIZE.NEXTVAL, sub.* from(
SELECT #{sizeVO.s_p_code,jdbcType=VARCHAR}, #{sizeVO.s_size,jdbcType=VARCHAR} FROM dual
) sub

INSERT INTO tbl_size(s_code, s_p_code, s_size) SELECT SEQ_SIZE.NEXTVAL, sub.* from(
SELECT #{sizeVO.s_p_code,jdbcType=VARCHAR}, #{sizeVO.s_size,jdbcType=VARCHAR} FROM dual
) sub

INSERT INTO tbl_color(c_code, c_s_code, c_color, c_qty) SELECT SEQ_COLOR.NEXTVAL, sub.* from(
SELECT #{color.c_s_code,jdbcType=VARCHAR}, #{color.c_color,jdbcType=VARCHAR}, #{color.c_qty,jdbcType=VARCHAR} FROM dual
) sub

INSERT INTO tbl_p_images(p_img_seq, p_img_p_code, p_img_origin_name, p_img_upload_name) SELECT SEQ_PRODUCT_IMAGE.NEXTVAL, sub.* from(
SELECT #{img.p_img_p_code,jdbcType=VARCHAR}, #{img.p_img_origin_name,jdbcType=VARCHAR}, #{img.p_img_upload_name,jdbcType=VARCHAR} FROM dual
) sub

mapper.update:
UPDATE tbl_product SET
	p_name = #{p_name},
	p_price = #{p_price},
	p_desc = #{p_desc,jdbcType=VARCHAR},
	p_image = #{p_image,jdbcType=VARCHAR}

WHERE p_code = #{p_code}
